public function getMenus()
    {
			 // Check if BigKahuna exists first
       if (!Folder::disk('storage')->exists('addons/BigKahuna')) {
         return false;
       }

			 // Gather all the BigKahuna files into a Laravel collection
       $data = collect(Folder::disk('storage')->getFiles('addons/BigKahuna'));

       $menus = $data->map(function ($menu, $key) {
           $basename = basename($menu, '.json');
           $array = $this->getMenuItems($menu)->map(function ($i, $key) {
             $item = $this->formatItemForMenu($i);

             // Format submenu items
             foreach ($item['items'] as $key => $value) {
               $item['items'][$key] = $this->formatItemForMenu($value);
             }

             return $item;
           });

           return array(
             'slug' => $basename,
             'items' => $array
           );
         });

       return $menus;
    }

    private function getMenuItems($filename)
    {
      return collect(json_decode(File::disk('storage')->get($filename), true));
    }

    private function formatItemForMenu($i) {
      $collection_names = collect(Collection::handles());
      $taxonomy_names = collect(Taxonomy::handles());

      $item = $i;
      $type = Str::slug($i['type']);

      // If item is a Collection item or a Taxonomy
      // change the type to reflect that
      if ($collection_names->contains(Str::slug($i['type']))) {
        $type = 'collection';
      } elseif ($taxonomy_names->contains(Str::slug($i['type']))) {
        $type = 'taxonomy';
      }

      switch ($type) {
        case 'pages':
          $content = Content::find($i['id']);
          if ($content) {
            $item['uri'] = $content->uri();
          } else {
            $item['uri'] = '#';
          }
          break;
        case 'custom':
          $item['uri'] = $i['url'];
          break;
        case 'collection':
          $content = Content::find($i['id']);
          if ($content) {
            $item['uri'] = $content->uri();
          } else {
            $item['uri'] = '#';
          }
          break;
        case 'taxonomy':
          $parts = explode('/', $i['id']);
          $term = Term::whereSlug($parts[1], $parts[0]);
          if ($term) {
            $item['uri'] = $term->uri();
          } else {
            $item['uri'] = '#';
          }
          break;
      }

      return $item;
    }

public function getStaff()
    {
      $users = User::all();

      return collect($users)->map(function ($user) {
        $user_data = $user->data();
        return array(
          'username' => $user->username(),
          'id' => $user->id(),
          'name' => isset($user_data['name']) ? $user_data['name'] : NULL,
          'title' => isset($user_data['title']) ? $user_data['title'] : NULL,
          'photo' => isset($user_data['photo']) ? $user_data['photo'] : NULL,
          'content' => $user->content()
        );
      });
    }

async nuxtServerInit ({ commit }, { app }) {
    const { data } = await app.$axios.get('/api/everything', {
      useCache: true
    })

    const { menus, staff, collections, pages, taxonomies, globals, version } = data

    commit('SET_VERSION', version)
    commit('SET_TAXONOMIES', taxonomies)
    commit('SET_CASE_STUDIES', collections['case-studies'])
    commit('SET_PAGES', pages)
    commit('SET_USERS', staff)
    commit('SET_GLOBALS', globals[0])
    commit('SET_MENUS', menus)
    commit('SET_FORMS', collections.forms)
    commit('SET_ORIGINALS', collections.blog)

    return data
		// Don't forget to return your awaited data,
		// so Nuxt knows to wait for it before rendering pages :)
  }

import { cacheAdapterEnhancer } from 'axios-extensions'
import LRUCache from 'lru-cache'
const ONE_HOUR = 1000 * 60 * 60

const defaultCache = new LRUCache({ maxAge: ONE_HOUR })

export default function({ $axios }) {
  const defaults = $axios.defaults
  // https://github.com/kuitos/axios-extensions
  defaults.adapter = cacheAdapterEnhancer(defaults.adapter, {
    enabledByDefault: false,
    cacheFlag: 'useCache',
    defaultCache
  })
}

